// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import TourCard from './components/TourCard';
import TourDetail from './components/TourDetail';
import Cart from './components/Cart';

const App = () => {
  const [tours, setTours] = useState([
    { id: 1, title: 'Safari Adventure', shortDescription: 'A thrilling safari experience.', description: 'Detailed description of Safari Adventure.' },
    { id: 2, title: 'City Lights Tour', shortDescription: 'Explore the city lights.', description: 'Detailed description of City Lights Tour.' },
  ]);

  const [cartItems, setCartItems] = useState([]);

  const addToCart = (tour) => {
    setCartItems([...cartItems, tour]);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<div>{tours.map((tour) => <TourCard key={tour.id} tour={tour} />)}</div>} />
          <Route path="/tour/:id" element={<TourDetail tours={tours} addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cartItems={cartItems} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
